    public function exportContracts()
    {
        // Fetch the contract data for the authenticated user
        $contracts = StoreContract::where('user_id', auth()->id())->where('contracttype' , 'normalcontract')->get();


        $filename = 'contracts_' . date('Ymd') . '.csv';

        // Create a file pointer connected to the output stream
        $handle = fopen('php://output', 'w');

        // Set the headers for the CSV download
        header('Content-Type: text/csv');
        header('Content-Disposition: attachment; filename="' . $filename . '"');

        // Add the CSV column headers
        fputcsv($handle, [
            'ID', 
            'File Type', 
            'File Size', 
            'Real File Name', 
            'File Path', 
            'User Name', 
            'Contract Name', 
            'Contract Type', 
            'Division', 
            'Vendor Name', 
            'Legal Entity Status', 
            'Start Date', 
            'End Date', 
            'Contract Value', 
            'Signing Status', 
            'Renewal Terms', 
            'Payment Terms', 
            'Fee Escalation Clause', 
            'User ID', 
            'Created At', 
            'Updated At'
        ]);

        // Loop through the contracts and add them to the CSV
        foreach ($contracts as $contract) {
            fputcsv($handle, [
                $contract->id,
                $contract->file_type,
                $contract->file_size,
                $contract->real_file_name,
                $contract->file_path,
                $contract->user_name,
                $contract->contract_name,
                $contract->contracttype,
                $contract->divison,
                $contract->vendor_name,
