    public function createFolder(Request $request)
    {
        
        // $request->validate([
        //     'folder_name' => ['required', 'regex:/^[a-zA-Z0-9\s\-\(\):]+$/'],
        //     // 'parent_folder' => ['nullable', 'regex:/^[a-zA-Z0-9\s\/&_]+$/'],
