            'password' => Hash::make($data['password']),
            'role' => $data['role'],
            'name' => $data['name'],
            'plan_type' => $data['plan_type'],
            // 'tenure_start_date' => $data['tenure_start_date'],
            // 'tenure_end_date' => $data['tenure_end_date'],

